package com.xworkz.inheretence.external;

public class Playlist {

        public Playlist() {
            System.out.println("No-args constructor of Playlist");
        }

        public void addSong() {
            System.out.println("Adding a song to the playlist...");
        }

        public void removeSong() {
            System.out.println("Removing a song from the playlist...");
        }

        public void playPlaylist() {
            System.out.println("Playing the playlist...");
        }

        public void shufflePlaylist() {
            System.out.println("Shuffling the playlist...");
        }

        public void displayPlaylist() {
            System.out.println("Displaying the playlist...");
        }
    }
